package com.example.caroline.foodme;

import android.support.v7.app.AppCompatActivity;
import android.os.Bundle;
import android.support.v7.widget.DefaultItemAnimator;
import android.support.v7.widget.LinearLayoutManager;
import android.support.v7.widget.RecyclerView;
import android.util.Log;

import java.util.ArrayList;

public class AccountSetUpActivity extends AppCompatActivity {

    private RecyclerView recyclerView;
    private RecyclerView.LayoutManager layoutManager;
    private SetupAdapter setupAdapter;
    private ArrayList<SetupItem> setupItems;
    private ArrayList<SetupItem> selectedItems;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_account_set_up);
        setupItemsCreate();

        recyclerView = findViewById(R.id.recyclerView);
        layoutManager = new LinearLayoutManager(this);
        recyclerView.setLayoutManager(layoutManager);
        recyclerView.setItemAnimator(new DefaultItemAnimator());
        setupAdapter = new SetupAdapter(setupItems, this, this);
        recyclerView.setAdapter(setupAdapter);

    }

    public void setupItemsCreate(){
        setupItems = new ArrayList<SetupItem>();
        //form the arraylist of stuff here
        setupItems.add(new SetupItem("carrot", "https://www.organicfacts.net/wp-content/uploads/Carrot1.jpg"));
        setupItems.add(new SetupItem("potato", "data:image/jpeg;base64,"));
        selectedItems = new ArrayList<>();
    }

    public void addtoSelectedItems(int i){
        selectedItems.add(setupItems.get(i));
        Log.d("AccountSetUp", "added item");
    }
    public void deleteFromSelectedItems(int i){
        selectedItems.remove(setupItems.get(i));
    }
}
